import React from "react";
import {Dropdown} from 'react-bootstrap'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMagicWandSparkles,
  faEllipsis,
} from "@fortawesome/free-solid-svg-icons";
const listTitle = (props) => {
  return (
    <div className="listTitle">
      <FontAwesomeIcon
        className="headingIcon"
        icon={faMagicWandSparkles}
        size="xl"
      />
      <div className="titleContent">
        <h3 className="heading">{props.listName}</h3>
        <p className="jobCount">{props.jobCount} Jobs</p>
      </div>

      <Dropdown>
          <Dropdown.Toggle variant="outline-light" Transparent button id="dropdown-basic">
            <FontAwesomeIcon className="moreIcon" icon={faEllipsis} size="xl" />
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item
              as="button"
              onClick={() => {
                return props.delList(props.index);
              }}
            >
              Delete
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
    </div>
  );
};

export default listTitle;
