import { React, useState } from "react";
import ListTitle from "./listTitle";
import CompanyCard from "./CompanyCard";
import { Button, Modal, Form, Row, Col, Container } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPlus } from "@fortawesome/free-solid-svg-icons";
import "./listContainer.css";

function AddModal(props) {
  return (
    <Modal
      {...props}
      size="sm"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Label>Company</Form.Label>
          <Form.Control type="text" placeholder="Company" autoFocus />
          <Form.Label>Job Title</Form.Label>
          <Form.Control type="email" placeholder="Job Title" />
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Discard</Button>
        <Button onClick={props.onHide}>Save</Button>
      </Modal.Footer>
    </Modal>
  );
}

const ListContainer = (props) => {
  const [modalShow, setModalShow] = useState(false);
  return (
    <div className="listContainer ">
      <ListTitle listName={props.listName} />
      <button className="addCardBtn" onClick={() => setModalShow(true)}>
        <FontAwesomeIcon icon={faPlus} size="xs" />
      </button>
      <AddModal show={modalShow} onHide={() => setModalShow(false)} />
      <div className="cardList">
        {props.cardsList.map((card, index) => (
          <CompanyCard role={card.role} companyName={card.companyName} />
        ))}
        {/* <CompanyCard role={props.cardsList[0].role} /> */}
      </div>
    </div>
  );
};

export default ListContainer;
