import React,{useState} from "react";
import { Dropdown } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faMagicWandSparkles,
  faEllipsis,
} from "@fortawesome/free-solid-svg-icons";
import { useDispatch } from "react-redux";
import { deleteList } from "../../redux/listsSlice";

const ListTitle = (props) => {
  const [title, setTitle] = useState(props.listName);
  function handleTitle(event) {
    const newValue = event.target.value;
    setTitle(newValue);
  }
  const dispatch = useDispatch();
  function delList(index) {
    dispatch(deleteList(index));
    console.log(index)
  }
  return (
    <div className="listTitle">
      <FontAwesomeIcon
        className="headingIcon"
        icon={faMagicWandSparkles}
        size="lg"
      />
      <div className="titleContent">
        <input onChange={handleTitle} type="text" placeolder="List Name" className="heading" value={title}></input>
        <p className="jobCount">{props.jobCount} Jobs</p>
      </div>

      <Dropdown>
        <Dropdown.Toggle
          variant="outline-light"
          id="dropdown-basic"
        >
          <FontAwesomeIcon className="moreIcon" icon={faEllipsis} size="xl" />
        </Dropdown.Toggle>

        <Dropdown.Menu>
          <Dropdown.Item as="button" onClick={()=> delList(props.index)}>
            Delete
          </Dropdown.Item>
        </Dropdown.Menu>
      </Dropdown>
    </div>
  );
};

export default ListTitle;
