import React, { useState } from "react";
import { Button, Modal, Form } from "react-bootstrap";
import {useDispatch } from 'react-redux'
import {addCard} from "../../redux/listsSlice";
import styles from './Counter.module.css'


const InputModal = (props) => {
  const [company, setCompany] = useState("");
  const [jobTitle, setJobTitle] = useState("");
  const dispatch = useDispatch();

  function handleInputCompany(event) {
    const newValue = event.target.value;
    setCompany(newValue);
  }
  function handleInputJobTitle(event) {
    const newValue = event.target.value;
    setJobTitle(newValue);
  }
  console.log(company);
  console.log(jobTitle);

  return (
    <Modal
      {...props}
      size="sm"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Label>Company</Form.Label>
          <Form.Control
            onChange={handleInputCompany}
            type="text"
            placeholder="Company"
            value={company}
            autoFocus
          />
          <Form.Label>Job Title</Form.Label>
          <Form.Control
            onChange={handleInputJobTitle}
            type="text"
            placeholder="Job Title"
            value={jobTitle}
          />
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Discard</Button>
        <Button onClick={() => dispatch(addCard())}>Save</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default InputModal;
